package org.example.ui;import org.example.model.ConverterModel;import org.example.service.ConverterService;import org.example.util.CurrencyConverter;import java.util.Scanner;public class Menu {    private final ConverterService converterService = new ConverterService();    private final Scanner sc = new Scanner(System.in);    public void start() {        int opt = 0;        while (opt != 7) {            System.out.println("""                                    ==============================                CONVERSOR DE MOEDAS                ==============================                Escolha uma opção:                                1) USD → BRL                2) BRL → USD                3) USD → EUR                4) EUR → USD                5) BRL → EUR                6) EUR → BRL                7) Sair                """);            System.out.print("Opção: ");            opt = sc.nextInt();            if (opt == 7) {                System.out.println("Programa encerrado. Até logo!");                break;            }            convertFlow(opt);        }    }    private void convertFlow(int option) {        String base = null;        String target = null;        switch (option) {            case 1 -> { base = "USD"; target = "BRL"; }            case 2 -> { base = "BRL"; target = "USD"; }            case 3 -> { base = "USD"; target = "EUR"; }            case 4 -> { base = "EUR"; target = "USD"; }            case 5 -> { base = "BRL"; target = "EUR"; }            case 6 -> { base = "EUR"; target = "BRL"; }            default -> {                System.out.println("Opção inválida!");                return;            }        }        try {            System.out.print("Digite o valor a converter: ");            double value = sc.nextDouble();            ConverterModel model = converterService.getRates(base);            Double rate = model.getConversion_rates().get(target);            if (rate == null) {                System.out.println("Taxa não encontrada para " + target);                return;            }            double result = CurrencyConverter.convert(value, rate);            System.out.println("\nResultado:");            System.out.printf("%.2f %s = %.2f %s%n%n", value, base, result, target);        } catch (Exception e) {            System.out.println("Erro ao converter: " + e.getMessage());        }    }}